function checkPollen(e){fetch("./py/allergens-description-"+e+".txt").then(e=>e.text()).then(e=>{$("#allergy_placeholder").show(),$("#allergy").click(function(){$("#allergy_placeholder").hide(),document.getElementById("allergy_description").textContent=e,$("#allergy_description").show()}),fetch("./py/allergens-date_range.txt").then(e=>e.text()).then(e=>{console.log("Allergy description date range:",e)})})}$("#allergy").hide();